mapper.select:
		SELECT ITEMID
		FROM CART
 		WHERE USERID = #{username}

SELECT QTY
FROM CART
WHERE USERID = #{username} AND ITEMID = #{itemId}

mapper.update:
UPDATE CART
SET QTY = QTY + 1
WHERE USERID = #{username} AND ITEMID = #{itemId}

UPDATE CART
SET QTY = #{qty}
WHERE USERID = #{item.username} AND ITEMID = #{item.itemId}

mapper.delete:
DELETE
FROM CART
WHERE USERID = #{username} AND ITEMID = #{itemId}
